import CoreLocationclass LocationManager: NSObject, ObservableObject, CLLocationManagerDelegate {    private let locationManager = CLLocationManager()    @Published var location: CLLocation?    override init() {        super.init()        locationManager.delegate = self        locationManager.desiredAccuracy = kCLLocationAccuracyBest    }    func requestWhenInUseAuthorization() {        locationManager.requestWhenInUseAuthorization()    }    func locationManager(_ manager: CLLocationManager, didUpdateLocations locations: [CLLocation]) {        location = locations.last    }}