import SwiftUIimport GoogleMapsimport GooglePlaces// Model to store bar location datastruct BarLocation {    let name: String    let description: String    let latitude: Double    let longitude: Double}struct GoogleMapViewRepresentable: UIViewRepresentable {    var location: CLLocation?        // Hard-coded bar locations directly in this file    private let barLocations: [BarLocation] = [        BarLocation(name: "Harry's Chocolate Shop", description: "Popular campus bar", latitude: 40.42378, longitude: -86.90908),        BarLocation(name: "Brothers Bar & Grill", description: "Sports bar with food", latitude: 40.42415, longitude: -86.90838),        BarLocation(name: "The Tap", description: "Craft beer bar", latitude: 40.42305, longitude: -86.90683),        BarLocation(name: "Where Else", description: "Youthful bar", latitude: 40.42407, longitude: -86.90854),        BarLocation(name: "9 Irish Brothers", description: "Cozy vibe and great Guinness", latitude: 40.42306, longitude: -86.90321),        BarLocation(name: "Neon Cactus", description: "Giant club on campus", latitude: 40.42329, longitude: -86.90065)        // Add more bars as needed    ]        func makeUIView(context: Context) -> GMSMapView {        let options = GMSMapViewOptions()        options.camera = GMSCameraPosition.camera(withLatitude: 40.429595015286466, longitude: -86.90914839190164, zoom: 8)        let mapView = GMSMapView(options: options)        mapView.isMyLocationEnabled = true  // This enables the blue location dot        mapView.settings.myLocationButton = true                // Add bar markers during initial setup        addBarMarkers(to: mapView)                return mapView    }        func updateUIView(_ uiView: GMSMapView, context: Context) {        uiView.clear() // Clear existing markers                // Re-add all bar markers        addBarMarkers(to: uiView)                if let location = location {            // Only animate camera to user location without adding a marker            let camera = GMSCameraPosition.camera(withLatitude: location.coordinate.latitude,                                                 longitude: location.coordinate.longitude,                                                 zoom: 15)            uiView.animate(to: camera)        }    }        // Helper function to add bar markers to the map    private func addBarMarkers(to mapView: GMSMapView) {        let pinkColor = UIColor(red: 1.0, green: 0.4, blue: 0.7, alpha: 1.0) // Bright pink                for barLocation in barLocations {            let marker = GMSMarker(position: CLLocationCoordinate2D(latitude: barLocation.latitude,                                                                   longitude: barLocation.longitude))            marker.title = barLocation.name            marker.snippet = barLocation.description                        // Use the standard Google Maps pin but in pink            marker.icon = GMSMarker.markerImage(with: pinkColor)                        // Make the marker appear with animation            marker.appearAnimation = .pop            marker.map = mapView        }    }}