import SwiftUIimport GoogleMapsimport GooglePlacesstruct GoogleMapViewRepresentable: UIViewRepresentable {    var location: CLLocation?    func makeUIView(context: Context) -> GMSMapView {        let options = GMSMapViewOptions()        options.camera = GMSCameraPosition.camera(withLatitude: 40.429595015286466, longitude: -86.90914839190164, zoom: 5)        let mapView = GMSMapView(options: options)        mapView.isMyLocationEnabled = true        mapView.settings.myLocationButton = true        return mapView    }        func updateUIView(_ uiView: GMSMapView, context: Context) {        uiView.clear() // Clear existing markers        if let location = location {            let position = CLLocationCoordinate2D(latitude: location.coordinate.latitude, longitude: location.coordinate.longitude)            let marker = GMSMarker(position: position)            marker.title = "Current Location"            marker.icon = GMSMarker.markerImage(with: .blue)            marker.map = uiView                        // Animate camera to new location            let camera = GMSCameraPosition.camera(withLatitude: location.coordinate.latitude, longitude: location.coordinate.longitude, zoom: 15)            uiView.animate(to: camera)        }    }}//test